var base64 = require('./index');

const init = async () => {
    base64.encode('./text.txt')
    .then((base64String) => {
        console.log(base64String)
    })
    .catch((err) => {
        console.log(err)
    })

    const base64String = await base64.encode('./text.txt')

    base64.decode(base64String, 'text.new.txt')
    .then((base64String) => {
        console.log(base64String)
    })
    .catch((err) => {
        console.log(err)
    })

    await base64.decode(base64String, './text.await.txt')
}

init()